// CartProducts.js
import React from 'react';
import styles from "./cart.css"

const CartProducts = ({ cart }) => {


  return (
    <div>
      <h2>Carrito de Compras</h2>
      {cart.length === 0 ? (
        <p>El carrito está vacío</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
                <button className='button__less'>-</button>
                <p>{item.quantity}</p>
                <button className='button__plus'>+</button>
                <p>{item.title}</p>
                <p>{item.price}</p>



                <img className='img__cart' src={item.image}/>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CartProducts;
