// CartProducts.js
import React from 'react';
import styles from "./cart.css"

const CartProducts = ({ cart, removeItemOnCart, increaseQuantity, decreaseQuantity, clearCart }) => {

  const totalToPay = () => cart.reduce((total, item) => total + (item.quantity * item.price), 0)


  return (
    <div className='shopping__cart'>
      <>
        <div className='div__cartTitle'>
          <h2>Shopping Cart</h2>
          <p className='close__cart'>X</p>
          </div>
      </>  
      {cart.length === 0 ? (
        <p>Shopping cart is empty</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>

                  <div className='div__buttons'>
                    <button onClick={() => decreaseQuantity(item.id)} className='button__less'>-</button>
                    <p>{item.quantity}</p>
                    <button onClick={() => increaseQuantity(item.id)} className='button__plus'>+</button>
                  </div>
                    <p>{item.title}</p>
                    <p>$  {item.price}</p>
                    <img className='img__cart' src={item.image}/>
                    <button className='delete__product' onClick={() => removeItemOnCart(item.id)}>x</button>

            </li>
          ))}
          <button onClick={clearCart} className='empty__button'>Empy Cart</button>
          <p>Total a pagar : ${totalToPay()}</p>
        </ul>
      )}
    </div>
  );
};

export default CartProducts;
