import React from "react";
import "./styles.css"; // Asegúrate de que los estilos están importados correctamente

const Cart = ({ cart }) => {
    return (
        <div className="cart">
            <h2>Carrito de Compras</h2>
            {cart.length === 0 ? (
                <p>El carrito está vacío</p>
            ) : (
                <ul>
                    {cart.map((item, index) => (
                        <li key={index}>
                            <h3>{item.title}</h3>
                            <p>Precio: ${item.price}</p>
                            <img className="img__product" src={item.image} alt={item.title} />
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default Cart;
