import React, { useState } from "react";
import Header from "./components/Header";
import ProductList from "./components/ProductsList";
import CartProducts from "./components/CartProducts";

function App() {
  const [cart, setCart] = useState([]);
  const [cartVisible, setCartVisible] = useState(false)

  const toggleCartVisible = () => {
    setCartVisible(!cartVisible)
  }

  const addToCart = (item) => {

  const itemExist = cart.findIndex(product => product.id === item.id)

    if(itemExist >= 0){
      const updatedCart = [...cart];
      updatedCart[itemExist].quantity++;
      setCart(updatedCart)
      alert('El producto ya existe en el carrito. Puedes hacer el uso de los botones + y - en el producto en el carrito para agregar mas prodictos del mismo')
    } else {
      item.quantity = 1;  
      setCart([...cart, item])
    }
  }

  const removeItemOnCart = (id) => {
      setCart(prevCart => prevCart.filter(product => product.id !== id))
  }

  const maxQty = 5
  const minQty = 1

  const increaseQuantity = (id) => {
    const updatedCart = cart.map((item) => {
      if(item.id === id && item.quantity < maxQty) {
        return {
          ...item,
          quantity: item.quantity + 1
        }
      }
      return item
    })
    setCart(updatedCart)
  }

  const decreaseQuantity = (id) => {
    const updatedCart = cart.map((item) => {
      if(item.id === id && item.quantity !== minQty) {
        return {
          ...item,
          quantity: item.quantity - 1
        }
      }
      return item
    })
    setCart(updatedCart)
  }

  const clearCart = () => {
    setCart([])
  }

  return (
    <div className="App">
      <Header toggleCartVisibility={toggleCartVisibility} />
      {cartVisible && (
        <CartProducts 
          cart={cart}
          removeItemOnCart={removeItemOnCart}
          increaseQuantity={increaseQuantity}
          decreaseQuantity={decreaseQuantity}
          clearCart={clearCart}
        />
      )}
    </div>
  );
};


export default App;
