import React, { Fragment, useEffect, useState } from "react";
import styles from "./styles.css";
import axios from "axios";
import Cart from "./Cart"

const ProductsList = () => {
    const [ products, setProducts] = useState([]);
    const [ cart, setCart] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get(`https://fakestoreapi.com/products`)
                setProducts(response.data)
                console.log('Response data:', response.data);
            } catch (error) {
                console.error(error);
            }
        }
        fetchProducts();
    }, []);

    const addToCart = (product) => {
        setCart([...cart, product]);
        console.log(`Producto agregado al carrito: ${product.title}`);
    };


    return(
        <Fragment>
            <h2>Productos</h2>
            <section>
                {products.map((product) => {
                    const {id, title, price, category, image, description} = product;

                    return (
                        <>
                            <div className="product__box">
                                <article key={id}>
                                    <h3 className="product__title">{title}</h3>
                                    <img className="img__product" src={image} />
                                    <p>Precio ${price}</p>
                                    <p>Categoría: {category}</p>
                                    <p className="description">Descripción: {description}</p>
                                </article>
                                <div>
                                <button className="buy__button" onClick={() => addToCart(product)}>Agregar a Carrito</button>
                                </div>
                            </div>
                        </>
                    )
                })

                }
            </section>
        </Fragment>
    )
};

export default ProductsList;
