import React, { Fragment, useEffect, useState } from "react";
import styles from "./styles.css";
import axios from "axios";

const ProductsList = ({addToCart}) => {
    const [ products, setProducts] = useState([]);
    const [ cartProducts, setCartProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get(`https://fakestoreapi.com/products`)
                setProducts(response.data)
            } catch (error) {
                console.error(error);
            }
        }
        fetchProducts();
    }, []);

    return(
        <Fragment>
            <h2>Products</h2>
            <section>
                {products.map((product) => {
                    const {id, title, price, category, image, description} = product;

                    return (
                        <>
                            <div className="product__box">
                                <article key={id}>
                                    <h3 className="product__title">{title}</h3>
                                    <img className="img__product" src={image} />
                                    <p className="price">${price}</p>
                                    <p>Categoría: {category}</p>
                                    <p className="description">Descripción: {description}</p>
                                </article>
                                <div>
                                <button className="buy__button" onClick={() => addToCart(product)}>Add to Cart</button>
                                </div>
                            </div>
                        </>
                    )
                })

                }
            </section>
        </Fragment>
    )
};

export default ProductsList;
