import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

const ProductsList = ({ addToCart }) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await axios.get(`https://fakestoreapi.com/products`);
                setProducts(response.data);
                console.log('Response data:', response.data);
            } catch (error) {
                console.error(error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <Fragment>
            <h2>Productos</h2>
            <section>
                {products.map((product) => {
                    const { id, title, price, category, image, description } = product;

                    return (
                        <div className="product__box" key={id}>
                            <article>
                                <h3 className="product__title">{title}</h3>
                                <img className="img__product" src={image} alt={title} />
                                <p>Precio: ${price}</p>
                                <p>Categoría: {category}</p>
                                <p className="description">Descripción: {description}</p>
                            </article>
                            <div>
                                <button className="buy__button" onClick={() => addToCart(product)}>Agregar a Carrito</button>
                            </div>
                        </div>
                    );
                })}
            </section>
        </Fragment>
    );
};

export default ProductsList;
