import styles from "./header.css";
import logo from "./img/tiendaLogo.png";
import logoCart from "./img/cartLogo.svg";
import logoMenu from "./img/menu.svg";

const Header = ({totalProducts, onClickLogoCart, onClickHelpIcon, onClickCloseContactIcon}) => {


    return (
        <>
            <header>
                <img src={logo} alt="Logo" className={styles.logo} />
                <h1>My Little Corner Store</h1>
                <div>
                    <span className="total__products">{totalProducts()}</span>
                    <img onClick={onClickLogoCart} src={logoCart} alt="logoCart" id="logo__cart" />
                </div>
                <p onClick={onClickHelpIcon} className="help">Need Help?</p>  
            </header>
            {showContact ? (<div className="contact">
                <div className="div__contactTitle">
                    <h3>Contact</h3>
                    <p onClick={onClickCloseContactIcon} className="close__contactIcon">Close</p>
                </div>
                <input type="text" placeholder="Name"></input>
                <input type="email" placeholder="email"></input>
                <input type="text" placeholder="Cellphone number"></input>
                <button>Send</button>
            </div>) : (null)}

            </>
    )
}

export default Header;
